path Y = (0, -3.5)--(0, 7.5);
draw(X); draw(Y);

for(int n=-6; n <= 5; ++n)
if( n != 0 )
draw( (n,0.25)--(n,-0.25) );
for(int n=-3; n <= 7; ++n)
if( n != 0 )
draw( (0.25,n)--(-0.25,n) );

