draw(rotate(ang)*ellipse((0,0),2*l+2*w,l*w*2/sqrt(l^2+w^2)));
label("$A$",(-l,w),NW);
label("$B$",(-l,-w),SW);
label("$C$",(l,-w),SE);
label("$D$",(l,w),NE);
