dot((1,0)); label("$D$", (1,0), NE);
dot((1,1)); label("$C=D'$", (1,1), E);
dot((0,1)); label("$B$", (0,1), NE);

draw((0,0)--(1,1)--(0,2)--(-1,1)--(0,0), blue);
dot((0,2)); label("$C'$", (0,2), NE);
dot((-1,1)); label("$B'$", (-1,1), W);

